mapper.insert:
INSERT INTO SHOP_ITEM(
    ITEM_CODE,
    ITEM_NAME,
    ITEM_PRICE,
    ITEM_INTRO,
    CATE_CODE
) VALUES (
    #{itemCode}
    ,   #{itemName}
    ,   #{itemPrice}
    ,   #{itemIntro}
    ,   #{cateCode}
);

INSERT INTO ITEM_IMG(
ORIGIN_FILE_NAME
, ATTACHED_FILE_NAME
, IS_MAIN
, ITEM_CODE
)VALUES

    (
    #{img.originFileName}
    , #{img.attachedFileName}
    , #{img.isMain}
    , #{itemCode}
    )

mapper.select:
UPDATE SHOP_ITEM
SET
    ITEM_STOCK = #{itemStock}
WHERE ITEM_CODE = #{itemCode}
