mapper.insert:
INSERT INTO HOSPITAL_MEMBER(
    MEM_ID
    , MEM_PW
    , MEM_NAME
    , MEM_TEL
    , GENDER
    , BIRTHDAY
    , EMAIL
) VALUES (
    #{memId}
    , #{memPw}
    , #{memName}
    , #{memTel}
    , #{gender}
    , #{birthday}
    , #{email}
);

mapper.select:
SELECT EMAIL
FROM HOSPITAL_MEMBER
WHERE EMAIL = #{email}

mapper.update:
UPDATE HOSPITAL_MEMBER
SET
    MEM_ID = #{memId}
    ,   MEM_PW = #{memPw}
    ,   EMAIL = #{email}
WHERE MEM_TEL = #{memTel}

mapper.insert:
INSERT INTO hospital_member
    (MEM_ID
    ,   MEM_NAME
    ,   BIRTHDAY
    ,   GENDER
    ,   EMAIL
    ,   MEM_PW
    ,   MEM_TEL)
VALUES(
    #{memId}
    ,   #{memName}
    ,   #{birthday}
    ,   #{gender}
    ,   #{email}
    ,   #{memPw}
    ,   #{memTel}
)

mapper.select:
SELECT MEM_ID, MEM_NAME, MEM_ROLE
FROM hospital_member
WHERE MEM_ID = #{memId}
AND MEM_PW = #{memPw};
